import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { List } from '@material-ui/core';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
const useStyles = makeStyles({

  root: {
    formControl: {
      margin: 1,
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: 2,
    },
    '& > *': {
      margin: '1',
      width: '25ch',
    },
    minWidth: 275,
    margin:'auto',
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function OutlinedCard() {
  const classes = useStyles();
  const [deposit,setDeposit] = useState(12) 
  const [currency,setCurrency] = useState(['ETH','BTC','USD'])
  const cur = currency.map((item)=>{return (
  <Typography variant="h5" key={item} component="h2">
  {item} <b>::</b> nev
</Typography>)
})
  // const [btc,setEth] = useState(123)
  // const [eth,setEth] = useState(123)
  const bull = <span className={classes.bullet}>•</span>;
  let mouse_ws = new WebSocket("ws://127.0.0.1:8000/ws/deposit/1/")
  mouse_ws.onopen = () => mouse_ws.send(JSON.stringify({ 'user_id': 1, 'amount': 30.3, 'currency': 'ETH' ,'action':'get'}));
  mouse_ws.onmessage = (evt) => {
    console.log(document.getElementById('select').value) 
  }

  // mouse_ws.test();
  return (
    <div className={classes.root}>

    <Card className={classes.root} variant="outlined">
      <CardContent>
        {cur}
          <Typography variant="body2" component="p">
          well meaning and kindly.
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent>
     <form className={classes.root} noValidate autoComplete="off">
     <FormControl required className={classes.formControl}>
      {/* <CardActions> */}
     <TextField className={classes.root} id="standard-basic" label="Buy" />
      {/* </CardActions> */}
        <InputLabel id="demo-simple-select-required-label">Age</InputLabel>
        <Select
          labelId="demo-simple-select-required-label"
          id="select"
          value={10}
          // onChange={handleChange}
          // className={classes.selectEmpty}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem id = 'etc' value={"ETH"}>ETH</MenuItem>
          <MenuItem id = 'btc' value={"BTC"}>BTC</MenuItem>
          <MenuItem id = 'usd' value={"USD"}>USD</MenuItem>
        </Select>
        <FormHelperText>Required</FormHelperText>
    <Button className={classes.root}>BUY</Button>
      </FormControl>

   </form>
    </Card>
    </div>
  );
}
